definition.script:
pipeline {
    agent { label 'built-in' }
    stages {
        stage('IaC Plan') {
            steps {
                sh 'cd /src/terraform && sed -i s/localhost/localstack/g providers.tf'
                sh 'cd /src/terraform && terraform init'
                sh """
                terraform validate
                terraform plan -out plan.txt
                """
            }
        }
        stage('IaC Apply') {
            steps {
                sh 'echo "Apply TF"'
                sh 'terraform apply -auto-approve plan.txt'
            }
        }
        stage('Docker Build') {
            steps {
                sh """
                    echo "Build step"
                    cd /src/app
                    docker build -t sa-code-test:latest .
                """
            }
        }
        stage('Docker Deploy') {
            steps {
                sh 'echo "docker run -d --network sa-code-test -p 8085:8080 sa-code-test:latest"'
            }
        }
    }
}
