definition.script:
pipeline {
    agent { label 'built-in' }
    stages {
        stage('IaC Plan') {
            steps {
                sh 'cd /src/terraform && sed -i s/localhost/ssss/g providers.tf'
                sh 'cd /src/terraform && terraform init'
                sh 'cd /src/terraform && terraform validate'
                sh 'cd /src/terraform && terraform plan -out plan_out'

            }
        }
        stage('IaC Apply') {
            steps {
                sh """
                    echo "Apply TF"
                    cd /src/terraform && terraform apply -auto-approve plan_out
                """

            }
        }
        stage('Docker Build') {
            steps {
                sh """
                    echo "Build step"
                    cd /src/app && docker build -t sa-code-test:latest .
                """
            }
        }
        stage('Docker Deploy') {
            steps {
                sh """
                sha_id="\$(docker images --no-trunc --quiet sa-code-test:latest)"
                echo "The sha of image is \${sha_id}"
                docker run -d --network sa-code-test -p 8080:8080 \${sha_id}

                """
            }
        }
    }
}
